package practice;

public class naturalnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int natural=100;
		int sum=0;
		for(int i=1;i<=natural;i++)
		{
			sum=sum+i;
			
		}
		System.out.println(sum);
		
		int nn=100;
		int k=0;
		int j=1;
		while(j<=nn)
		{
			k=k+j;
			j++;
					
		}
		
		System.out.println(k);

	}

}
